 import React,{useState} from 'react'
//useState:用来声明状态变量
/*
    用法：声明、读取、使用（修改）基本三项
 */

function ReactHooks() {
    let arr = [1,2,3,4,5,6]
    const [count,setCount] = useState(arr);
    // function add(){
    //   return count+2;
    // }
    function changeArr(){
      return arr.filter((item,index)=>{
        return item%2
      })
    }
    return(
        <div>
            <p>点击了{count}次</p>
            <button onClick={()=>{setCount(changeArr)}}>点我+1</button>
        </div>
    )
}

export default ReactHooks
